create table  Category(       Id                INTEGER not null primary key identity 	/*自增长编号*/,       Pid               INTEGER 	/*父编号*/,       Name              VARCHAR(255) 	/*名称*/,       Memo              VARCHAR(4000) 	/*注释*/,       CreatorId         INTEGER not null 	/*创建人编号 当前用户ID*/,       CreatorName       VARCHAR(255) 	/*创建人姓名*/,       CreateDate        DATETIME default getdate() null 	/*创建日期 默认为当前时间*/,       Status            TINYINT 	/*数据状态 0正常 1审核中 2被否决 -1已删除 */,       OrderNo           NUMERIC 	/*排序号*/);create table  Message(       Id                INTEGER not null primary key identity	/*编号*/,       Pid               INTEGER 	/*父编号*/,       Context         VARCHAR(4000) 	/*评论*/,       CreatorId         INTEGER not null 	/*创建人编号 当前用户ID*/,       CreatorName       VARCHAR(255) 	/*创建人姓名*/,       CreateDate        DATETIME default getdate() null 	/*创建日期 默认为当前时间*/,       Status           TINYINT 	/*数据状态 0正常 1审核中 2被否决 -1已删除 -2草稿*/,       HeadImg           VARCHAR(255) 	/*头像*/,       CloudId           INTEGER 	/*内容id*/);create table  Cloud(       Id                INTEGER not null primary key identity	/*编号*/,       Name              VARCHAR(255) 	/*名称*/,       Memo              VARCHAR(4000) 	/*注释*/,       TypeId            INTEGER 	/*类型*/,       CreatorId         INTEGER not null 	/*创建人编号 当前用户ID*/,       CreatorName       VARCHAR(255) 	/*创建人姓名*/,       CreateDate        DATETIME default getdate() null 	/*创建日期 默认为当前时间*/,       Status           TINYINT 	/*数据级别 0正常 1审核中 2被否决 -1已删除 -2草稿*/,       OrderNo           NUMERIC 	/*排序号*/);